import { Level } from "../helpers/imc"
import upImage from '../assets/up.png';
import downImage from '../assets/down.png';

type Props = {
    item: Level;
}

export const GridItem = ({item}:Props) => {
    return (
        <div className="rounded text-white flex flex-1 justify-center items-center flex-col" style={{backgroundColor: item.color}}>
            <div className='w-16 h-16 rounded-3xl flex justify-center items-center bg-black opacity-90'>
                <img src={item.icon === 'up' ? upImage : downImage} alt='up' width="30"/>
            </div>

            <div className="">
                {item.title}
            </div>

            <div className="">
                {item.imc}
            </div>
        </div>
    )
}
