import { useState } from 'react';

export const Container = () => {

    const [heightField, setHeightField] = useState<number>(0);
    const [weightField, setWeightField] = useState<number>(0);


    return (

        <div className="mx-auto flex max-w-4xl">
            <div className="flex-1 mr-8 bg-red-100">
                <h1 className='text-2xl'>Calcule o seu IMC.</h1>
                <p>IMC é a sigla para Índice de Massa Corpórea, parametro adotado pela Organizacao Mundial de Saúde para calcular
                    o peso ideal de cada pessoa.
                </p>

                <input type="number" value={heightField > 0 ? heightField : ''} placeholder="Digite a sua altura. Ex: 1.5 em metros" onChange={e => setHeightField(parseFloat(e.target.value))}   /> 
                <input type="number" value={weightField > 0 ? weightField : ''} placeholder="Digite o seu peso. Ex: 75.3 em quilos" onChange={e => setWeightField(parseFloat(e.target.value))}   /> 
            </div>
            <div className="flex-1 ml-8 bg-black">
                <p>Right side</p>
            </div>
        </div>
    )
}
